            array("llave" => "CC024", "nombre" => "Ejecutar labores de izaje de acuerdo a normativa internacional de señales manuales.", "definicion" => "Conocer y aplicar las señales manuales para labores de izaje.", "tipo_competencia_id" => 3, "proyecto" => "null", "alcance" => "Equipos Moviles"),
            array("llave" => "CC025", "nombre" => "Mantener distancia mínima de acercamiento de un vehículo y/o equipo en movimiento hacia un peatón de acuerdo a procedimiento de trabajo. (interacción hombre/máquina).", "definicion" => "Establecer y respetar la distancia mínima de acercamiento de un vehículo/equipo en movimiento hacia un peatón, ya sea por traslado u operación.", "tipo_competencia_id" => 2, "proyecto" => "PROYECTO DAND", "alcance" => "Equipos Moviles"),
